
public class instrução_While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int num = 0;
		
		while (num <= 70){
			
		if (num % 4 == 0) {
			System.out.println(num + " � divisivel por 4 ");
			
		}
		if (num % 2 != 0) {
			System.out.println(num + " � impar ");
		}
		num++;
		}
	
	}

}
